<?//for Product managementif (strpos($_SERVER["SCRIPT_NAME"],basename(__FILE__)) !== false){	header("location: index.php");	exit();}require_once('Config.php');class ProductManager extends commonObject{		function getAllProducts($show='1')		{			if($show == 0)				$arr_orders =  getData("SELECT * From products WHERE 1 order by is_active DESC");			elseif($show == 1)				$arr_orders =  getData("SELECT * From products WHERE is_active = 1");			elseif($show == 2)				$arr_orders =  getData("SELECT * From products WHERE is_active = 0");			return $arr_orders;		}				function get_allcounts()		{			$arr_counts = array();			$all = getOne("SELECT COUNT(product_id) AS CNT From products WHERE 1");			$arr_counts['all'] = $all;			$active = getOne("SELECT COUNT(product_id) AS CNT From products WHERE is_active = 1");			$arr_counts['active'] = $active;			$trash = getOne("SELECT COUNT(product_id) AS CNT From products WHERE is_active = 0");			$arr_counts['trash'] = $trash;			return $arr_counts;					}				function restore_product($id)		{			$change_data = array (									'is_active' => 1,																); 			$change_data['product_id'] = $id;			$updateQry=$this->getUpdateDataString($change_data,"products","product_id");			updateData($updateQry);					}				/*function getAllProducts()		{			return $resultSet = getData("SELECT * FROM `products` where is_active=1");		}*/				function getProductVariables()		{			$varArray['product_name'] = $_REQUEST['product_name'];			$varArray['item_code'] = $_REQUEST['item_code'];			$varArray['basic_cost'] = $_REQUEST['basic_cost'];						return $varArray;		}				function insertProduct($productArray)		{			$insertQry = $this->getInsertDataString($productArray, 'products');			updateData($insertQry);			return mysql_insert_id();		}			function getProductDetails($product_id)	{		return $resultSet = getRow("select * from  products where product_id='".$product_id."'");	}		function updateUsingId($productArray)	{			$updateQry=$this->getUpdateDataString($productArray,"products","product_id");			updateData($updateQry);	}		//To delete a row in products table using product_id. 	function deleteUsingId($product_id){		updateData("UPDATE `products` SET `is_active`=0 WHERE `product_id`='".$product_id."'");	}		function isItemCodeExist($item_code,$product_id=0){		$query="select * from products where item_code='".$item_code."'";		if($product_id!=0){			$query.=" AND product_id!=".$product_id;		}		$resultSet = getData($query);		if(sizeof($resultSet)>0){			return true;		}else{			return false;		}	}}?>